CREATE TABLE `tbl_products` (
  `product_id` int(11) NOT NULL COMMENT 'id sản phẩm',
  `product_name` varchar(100) DEFAULT NULL COMMENT 'tên sản phẩm',
  `product_alias` varchar(100) DEFAULT NULL COMMENT 'tên không dấu',
  `product_type_id` int(11) DEFAULT NULL COMMENT 'id loại sản phẩm',
  `product_description` text COMMENT 'miêu tả sản phẩm',
  `product_unit_price` float DEFAULT NULL COMMENT 'giá cả sản phẩm',
  `product_promotion_price` float DEFAULT NULL COMMENT 'giá ưu đãi',
  `product_image` varchar(255) DEFAULT NULL COMMENT 'hình ảnh sản phẩm',
  `product_unit` varchar(255) DEFAULT NULL COMMENT 'đơn vị',
  `product_new` int(11) DEFAULT NULL COMMENT 'sản phẩm mới',
  `created_at` timestamp NULL DEFAULT NULL COMMENT 'thời gian tạo',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT 'thời gian sửa'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

