  `product_image` varchar(255) DEFAULT NULL COMMENT 'hình ảnh sản phẩm',
  `product_unit` varchar(255) DEFAULT NULL COMMENT 'đơn vị',
  `product_new` int(11) DEFAULT NULL COMMENT 'sản phẩm mới',
  `product_size` varchar(100) DEFAULT NULL COMMENT 'kích thước sản phẩm',
  `product_color` varchar(255) DEFAULT NULL,
  `product_quantity` int(11) DEFAULT NULL COMMENT 'Số lượng sản phẩm',
  `created_at` timestamp NULL DEFAULT NULL COMMENT 'thời gian tạo',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT 'thời gian sửa'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

